import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SignUpPage.css';

function SignUpPage() {
    const [formValues, setFormValues] = useState({
        email: '',
        password: '',
        confirmPassword: '',
        username: '',   // שם משתמש
        role: 'user',   // תפקיד (ברירת מחדל: משתמש)
    });
    const [error, setError] = useState('');
    const navigate = useNavigate();

    // פונקציה לעדכון ערכי הטופס
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormValues({
            ...formValues,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const { email, password, confirmPassword, username, role } = formValues;

        // בדיקה האם הסיסמאות תואמות
        if (password !== confirmPassword) {
            setError('הסיסמאות אינן תואמות');
            return;
        }

        console.log('נתוני הטופס לפני שליחה:', formValues);

        try {
            const response = await axios.post('http://localhost:3001/api/auth/register', {
                email,
                password,
                username,
                role,
            });
            console.log('תגובת השרת:', response.data);
            navigate('/login'); // הפנייה לדף כניסה לאחר הצלחת הרישום
        } catch (error) {
            console.error('שגיאה בזמן הרישום:', error);
            if (error.response) {
                setError(error.response.data.message || 'שגיאה במהלך הרישום');
            }
        }
    };

    return (
        <div className="sign-up-page">
            <h2>הרשמה</h2>
            {error && <p className="error-message">{error}</p>}
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="email">אימייל:</label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        value={formValues.email}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="username">שם משתמש:</label>
                    <input
                        type="text"
                        id="username"
                        name="username"
                        value={formValues.username}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">סיסמה:</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={formValues.password}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="confirm-password">אישור סיסמה:</label>
                    <input
                        type="password"
                        id="confirm-password"
                        name="confirmPassword"
                        value={formValues.confirmPassword}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="role">תפקיד:</label>
                    <select
                        id="role"
                        name="role"
                        value={formValues.role}
                        onChange={handleInputChange}
                        required
                    >
                        <option value="user">משתמש</option>
                        <option value="admin">מנהל</option>
                    </select>
                </div>
                <button type="submit" className="btn">הירשם</button>
            </form>
        </div>
    );
}

export default SignUpPage;
