import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './LoginPage.css'; // Optional: For styling

function LoginPage({ onLogin }) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Make a login request to the server
            const response = await axios.post('http://localhost:3001/api/auth/login', { email, password });
            const { token, role } = response.data;

            // Store token and role in localStorage
            localStorage.setItem('token', token);
            localStorage.setItem('role', role); // Store user role for future use

            // Call the onLogin function passed from App.js to update the state there
            onLogin(role); 

            // Redirect based on the user's role
            if (role === 'admin') {
                navigate('/admin'); // Redirect to the admin page if user is admin
            } else if (role === 'user') {
                navigate('/'); // Redirect to home page for regular users
            }
        } catch (err) {
            setError('Invalid email or password');
        }
    };

    return (
        <div className="login-page">
            <h2 className="login">התחברות</h2>
            {error && <p className="error-message">{error}</p>}
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="email">אימייל:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">סיסמה:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit" className="btn">התחבר</button>
            </form>
        </div>
    );
}

export default LoginPage;
