// MetaDataDisplay.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const MetaDataDisplay = () => {
    const [metaData, setMetaData] = useState(null);

    useEffect(() => {
        const fetchMetaData = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api//meta');
                console.log('Fetched meta data:', response.data);
                setMetaData(response.data);
            } catch (error) {
                console.log('Error fetching meta data:', error);
            }
        };

        fetchMetaData();
    }, []);

    if (!metaData) {
        return <p>טוען נתונים...</p>;
    }

    return (
        <div className="meta-container">
            <h2>META DATA</h2>
            <p>מספר מנות דם בבנק: {metaData.totalBloodUnits}</p>
            <p>מספר תורמים רשומים: {metaData.totalDonors}</p>
            <p>ניפוקים אחרונים: {metaData.recentDispenses}</p>
            {/* ניתן להוסיף נתונים נוספים בהתאם לצורך */}
        </div>
    );
};

export default MetaDataDisplay;
