import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SignUpPage.css';

function SignUpPage() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (password !== confirmPassword) {
            setError('הסיסמאות אינן תואמות');
            return;
        }
        try {
            const url = 'http://localhost:3001/api/auth/register'; // Ensure you're using the correct port
            const { data: res } = await axios.post(url, { email, password });

            // Navigate to the login page after successful registration
            navigate("/login");
            console.log(res); // Print the server response in the console
        } catch (error) {
            if (error.response) {
                setError(error.response.data.msg || 'אירעה שגיאה');
            } else {
                setError('אירעה שגיאה');
            }
            console.error('Error during registration:', error);
        }
    };

    return (
        <div className="sign-up-page">
            <h2>הרשמה</h2>
            {error && <p className="error-message">{error}</p>}
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="email">דואר אלקטרוני:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">סיסמה:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="confirm-password">אישור סיסמה:</label>
                    <input
                        type="password"
                        id="confirm-password"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit" className="btn">הרשם</button>
            </form>
        </div>
    );
}

export default SignUpPage;
