import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DonorListDisplay = () => {
    const [donors, setDonors] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchDonors = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/donors');
                setDonors(response.data);
                setLoading(false);
            } catch (err) {
                setError('Error fetching donors');
                setLoading(false);
            }
        };

        fetchDonors();
    }, []);

    if (loading) {
        return <p>טוען תורמים...</p>;
    }

    if (error) {
        return <p>{error}</p>;
    }

    return (
        <div>
            <h2>רשימת תורמים</h2>
            <ul>
                {donors.map(donor => (
                    <li key={donor._id}>
                        {donor.donorName} - {donor.bloodType}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default DonorListDisplay;
