import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DonorListDisplay = () => {
    const [donors, setDonors] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchDonors = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/blood/donors');
                console.log('Fetched donors:', response.data); // Logging the response for debugging
                setDonors(response.data); // Set the donors state with the data
                setLoading(false); // Stop loading once the data is fetched
            } catch (err) {
                console.error('Error fetching donors:', err.response?.data || err.message); // Log the error
                setError('שגיאה בשליפת נתוני התורמים'); // Set a meaningful error message
                setLoading(false); // Stop loading if there's an error
            }
        };

        fetchDonors(); // Call the fetch function
    }, []); // Only run once when the component mounts

    if (loading) {
        return <p>טוען תורמים...</p>; // Show loading message while fetching
    }

    if (error) {
        return <p>{error}</p>; // Show the error message if an error occurred
    }

    if (donors.length === 0) {
        return <p>לא נמצאו תורמים</p>; // Handle case when no donors are found
    }

    return (
        <div>
            <h2>רשימת תורמים</h2>
            <ul>
                {donors.map(donor => (
                    <li key={donor._id}>
                        {donor.donorName} - {donor.bloodType}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default DonorListDisplay;
