import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DonorListDisplay = () => {
    const [donors, setDonors] = useState([]);
    const [loading, setLoading] = useState(true); // מצב טעינה ראשוני
    const [error, setError] = useState(null); // ניהול שגיאות

    useEffect(() => {
        axios
            .get('http://localhost:3001/api/blood/donors') // כתובת API לשליפת התורמים
            .then((res) => {
                console.log('Fetched donors:', res.data);
                setDonors(res.data); // שמירת התורמים ב-state הנכון
                setLoading(false); // סימון שהשליפה הסתיימה
            })
            .catch((err) => {
                console.log('Error fetching donors:', err);
                setError('שגיאה בשליפת נתוני התורמים'); // עדכון שגיאה
                setLoading(false); // סימון שהשליפה הסתיימה
            });
    }, []);

    if (loading) {
        return <p>טוען תורמים...</p>; // הצגת הודעת טעינה
    }

    if (error) {
        return <p>{error}</p>; // הצגת הודעת שגיאה במקרה של שגיאה
    }

    if (donors.length === 0) {
        return <p>לא נמצאו תורמים</p>; // הצגת הודעה כאשר אין תורמים
    }

    return (
        <div>
            <h2>רשימת תורמים</h2>
            <ul>
                {donors.map((donor) => (
                    <li key={donor._id}>
                        {donor.donorName} - {donor.bloodType}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default DonorListDisplay;
