import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DonorListDisplay = () => {
    const [donors, setDonors] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        axios
            .get('http://localhost:3001/api/blood/donors')
            .then((res) => {
                setDonors(res.data);
                setLoading(false);
            })
            .catch((err) => {
                setError(err.response?.data?.message || 'שגיאה בשליפת נתוני התורמים');
                setLoading(false);
            });
    }, []);

    if (loading) {
        return <p>טוען תורמים...</p>;
    }

    if (error) {
        return <p>{error}</p>;
    }

    if (donors.length === 0) {
        return <p>לא נמצאו תורמים</p>;
    }

    return (
        <div>
            <h2>רשימת תורמים</h2>
            <ul>
                {donors.map((donor) => (
                    <li key={donor._id}>
                        {donor.donorName} - {donor.bloodType}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default DonorListDisplay;
